import { Component, OnInit } from '@angular/core';
import { ParentService } from '../parent.service';
import { ParentTransaction } from '../parent.model';

@Component({
  selector: 'app-parent',
  templateUrl: './parent.component.html',
  styleUrls: ['./parent.component.css']
})
export class ParentComponent implements OnInit{
  parentTransactions: ParentTransaction[] = [];

  constructor(private parentService: ParentService){}

  ngOnInit(): void {
    this.getParentTransactions();
  }

  getParentTransactions(): void{
    this.parentService.getParentTransactions().subscribe(
      (transactions: ParentTransaction[])=>{
        this.parentTransactions = transactions;
      },
      (error) =>{
        console.log('Error Fetching Parent Transcations: ', error);
      }
    );
  }

  onClickTotalPaidAmount(){
    alert('section clicked !');
  }
}
<table class="table">
  <thead>
    <tr>
      <th scope="col">ID</th>
      <th scope="col">Sender</th>
      <th scope="col">Receiver</th>
      <th scope="col">Total Amount</th>
      <th scope="col">Total Paid Amount</th>
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let transaction of parentTransactions">
      <th scope="row">1</th>
      <td>{{ transaction.sender }}</td>
      <td>{{ transaction.receiver }}</td>
      <!-- <td>{{ transaction.totalAmount }}</td> -->
      <td (click)="onClickTotalPaidAmount()">{{ transaction.totalAmount }}</td>
      <!-- <td (click)="onClickTotalPaidAmount(parent.id)">100</td> -->
    </tr>
  </tbody>
</table>
